sys_script_include.script:
var NeedItState = Class.create();

//NeedItState.AWAITING = "Awaiting Approval";

NeedItState.prototype = {
    initialize: function() {
    },
	getCodeState: function(etiquetaEstado) {
	   var gCode = new GlideRecord("sys_choice");
	   gCode.addQuery("name","task");
	   gCode.addQuery("element","state");
	   gCode.addQuery("label",etiquetaEstado);
	   gCode.query();
	   if (gCode.next()){
		   //gs.addInfoMessage("Encuentra");
		   return gCode.getValue("value"); 	
	   }
	   else {
			gs.addInfoMessage("No encuentra");
	   }
	},
    type: 'NeedItState'
};
